mapper.update:
UPDATE m_commodity
commodityName = #{commodityName}, commodityNo = #{commodityNo}, supplierId = #{supplierId}, imgMain = #{imgMain}, specification = #{specification}, inPrice = #{inPrice}, salePrice = #{salePrice}, vipPrice = #{vipPrice}, tag = #{tag}, categoryId = #{categoryId}, info = #{info}, weight = #{weight}, commodityStatus = #{commodityStatus}, unitId= #{unitId}, stockNum = #{stockNum}, upperLimit = #{upperLimit}, lowerLimit = #{lowerLimit}, discount = #{discount}, status = #{status}, productionDate = #{productionDate}, guaranteeDay = #{guaranteeDay}, grade = #{grade}, storage = #{storage}, updateTime = sysdate()
where commodityId = #{commodityId}

mapper.select:
SELECT commodityCode,commodityName,commodityNo,supplierName,c.categoryName categoryName, unitName,inPrice/100 inPrice,salePrice/100 salePrice,vipPrice/100 vipPrice, case when discount=1 then '是' when discount=-1 then '否' end as discount, stockNum,upperLimit,lowerLimit, case when m.commodityStatus="0" then '启用' when m.commodityStatus="-1" then '禁用' end as commodityStatus FROM m_commodity m left join m_category c on m.categoryId=c.categoryId LEFT JOIN m_unit u ON u.unitId=m.unitId LEFT JOIN m_supplier s ON s.supplierId=m.supplierId where 1=1 and m.status=1
and m.storeId=#{storeId}
AND c.categoryId IN (SELECT categoryId FROM m_category WHERE parentId=#{categoryId})
and m.status=#{status}
and m.discount=#{discount}
AND ( m.commodityNo LIKE CONCAT(CONCAT('%', #{commodityNo}),'%') OR m.commodityName LIKE CONCAT(CONCAT('%', #{commodityNo}),'%') OR m.commodityCode LIKE CONCAT(CONCAT('%', #{commodityNo}),'%') )

update.foreach:
              update m_commodity 

	                  stockNum=#{item.stockNum},
	                  updateTime = #{item.updateTime} 

                  where commodityNo = #{item.commodityNo}
