mapper.select:
SELECT a.storeNo,a.storeId,a.storeName,a.userName,a.remain,a.phone,a.idCard,a.area,a.process,a.addTime,cds.deptName,s.`name`,s.supervisorId,top.totalPrice,tb.monthPrice,tb.monthNum,ca.totalVisitNum FROM t_store a LEFT JOIN sys_supervisor s ON a.supervisorId = s.supervisorNum LEFT JOIN ( SELECT userId,SUM(t.orderPrice)*0.01 AS totalPrice FROM t_order t WHERE `status` = 1 AND orderStatus !='-1' GROUP BY userId ) top ON a.userId = top.userId LEFT JOIN ( SELECT userId,SUM(orderPrice)*0.01 AS monthPrice,COUNT(1) AS monthNum FROM t_order WHERE DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(orderTime) AND `status` = 1 AND orderStatus !='-1' GROUP BY userId ) tb ON a.userId = tb.userId LEFT JOIN ( SELECT userId,COUNT(1) AS totalVisitNum FROM c_visit_record WHERE `status` = 0 AND visitResultStatus IS NOT NULL GROUP BY userId ) ca ON ca.userId = a.userId LEFT JOIN ( SELECT supervisorNum,deptName FROM sys_supervisor sss LEFT JOIN c_dept cd ON cd.deptId = sss.deptId ) cds ON a.supervisorId = cds.supervisorNum WHERE 1=1
AND a.supervisorId = #{relationMap.supervisor}
AND a.process = #{relationMap.process}
AND DATE_FORMAT(a.addTime,'%Y%m%d') >= DATE_FORMAT(#{relationMap.beginTime},'%Y%m%d')
AND DATE_FORMAT(a.addTime,'%Y%m%d') <= DATE_FORMAT(#{relationMap.endTime},'%Y%m%d')
ORDER BY totalPrice DESC

SELECT a.storeNo,a.storeId,a.storeName,a.userName,a.remain,a.phone,a.idCard,a.area,a.process,a.addTime,cds.deptName,s.`name`,s.supervisorId,top.totalPrice,tb.monthPrice,tb.monthNum,ca.totalVisitNum FROM t_store a LEFT JOIN sys_supervisor s ON a.supervisorId = s.supervisorNum LEFT JOIN ( SELECT userId,SUM(t.orderPrice)*0.01 AS totalPrice FROM t_order t WHERE `status` = 1 AND orderStatus !='-1' GROUP BY userId ) top ON a.userId = top.userId LEFT JOIN ( SELECT userId,SUM(orderPrice)*0.01 AS monthPrice, COUNT(1) AS monthNum FROM t_order WHERE DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(orderTime) AND `status` = 1 AND orderStatus !='-1' GROUP BY userId ) tb ON a.userId = tb.userId LEFT JOIN ( SELECT userId,COUNT(1) AS totalVisitNum FROM c_visit_record WHERE `status` = 0 AND visitResultStatus IS NOT NULL GROUP BY userId ) ca ON ca.userId = a.userId LEFT JOIN ( SELECT supervisorNum,deptName FROM sys_supervisor sss LEFT JOIN c_dept cd ON cd.deptId = sss.deptId ) cds ON a.supervisorId = cds.supervisorNum WHERE 1 = 1
AND a.supervisorId = #{supervisorId}
AND a.process = #{relationMap.process}
AND DATE_FORMAT(a.addTime,'%Y%m%d') >= DATE_FORMAT(#{beginTime},'%Y%m%d')
AND DATE_FORMAT(a.addTime,'%Y%m%d') <= DATE_FORMAT(#{endTime},'%Y%m%d')
ORDER BY totalPrice DESC

SELECT t.orderId,t.orderNumber,t.orderTime,t.number,t.orderPrice*0.01 AS orderPrice,t.orderStatus,td.orderDetailId,td.commodityName,td.commodityNum,td.commodityId,td.inPrice*0.01 AS inPrice,td.salePrice*0.01 AS salePrice,td.unit,td.imgMain FROM t_order t LEFT JOIN t_order_detail td ON t.orderNumber=td.orderNumber WHERE t.userId= #{relationMap.userId} AND t.`status`=1 AND t.orderStatus != '-1'
AND t.orderStatus =#{relationMap.orderStatus}
ORDER BY t.orderTime DESC
