mapper.select:
select m.tradeCode,m.cashNo,m.addTime,m.money/100 money,m.payType,c.`name` cashName from m_cashFlow m LEFT JOIN t_cashier c on c.cashierId=m.cashierId where m.storeId= #{relationMap.storeId} and m.`status`=1
AND m.userId=#{relationMap.userId}
AND DATE_FORMAT(m.addTime,'%Y%m%d') >= DATE_FORMAT(#{relationMap.beginTime},'%Y%m%d')
AND DATE_FORMAT(m.addTime,'%Y%m%d') <= DATE_FORMAT(#{relationMap.endTime},'%Y%m%d')
AND m.payType = #{relationMap.payType}
AND ( m.cashNo LIKE CONCAT(CONCAT('%',#{relationMap.fuzzyCondition}),'%') ) order by m.addTime

SELECT tradeCode, DATE_FORMAT( m.addTime, '%Y-%m-%d %H:%i:%S' ) `addTime`,m.storeId, round(money / 100,2) money, payType FROM m_cashFlow m
inner JOIN t_store s ON s.storeId=m.storeId where s.storeId=#{_parameter}
ORDER BY addTime desc LIMIT 10
