mapper.select:
SELECT s.userId,t.storeId,s.mobilePhone,t.storeName,t.userName,t.phone,s.locked,t.licenseId FROM sys_user s LEFT JOIN t_store t ON s.userId=t.userId WHERE 1=1 AND s.type='B'
AND s.locked = #{relationMap.status}
AND ( s.mobilePhone LIKE CONCAT(CONCAT('%', #{relationMap.name}),'%') OR t.storeName LIKE CONCAT(CONCAT('%', #{relationMap.name}),'%') )

mapper.update:
UPDATE sys_user

    locked=#{status}

WHERE 1=1
and userId = #{userId}

mapper.select:
SELECT banId,userId,item,type,money/100 money,status,addTime FROM t_balance
WHERE 1=1
AND userId=#{relationMap.userId}
