mapper.select:
SELECT o.orderNumber,o.orderTime,o.payTime,s.userName,o.orderPrice/100 orderPrice,o.platform,o.orderStatus,o.payCode FROM t_order o LEFT JOIN sys_user s ON o.userId=s.userId WHERE 1=1 and `status`=1
AND DATE_FORMAT(o.orderTime,'%Y%m%d') >= DATE_FORMAT(#{relationMap.beginTime},'%Y%m%d')
AND DATE_FORMAT(o.orderTime,'%Y%m%d') <= DATE_FORMAT(#{relationMap.endTime},'%Y%m%d')
AND o.orderStatus=#{relationMap.payType}
AND o.orderStatus !='1'
AND o.platform=#{relationMap.platform}
AND o.orderStatus=#{relationMap.orderStatus}
AND ( o.orderNumber LIKE CONCAT(CONCAT('%', #{relationMap.orderNumber}),'%') OR s.userName LIKE CONCAT(CONCAT('%', #{relationMap.orderNumber}),'%') )
order by o.orderTime DESC

SELECT d.commodityName,c.commodityNo,d.unit,d.commodityNum,d.salePrice/100 inPrice
FROM t_order_detail d LEFT JOIN t_commodity c ON d.commodityId=c.commodityId
WHERE d.orderNumber=#{orderNumber}

mapper.update:
UPDATE t_order
orderStatus=#{orderStatus}, arriveTime=sysdate()
WHERE 1=1 AND orderNumber=#{orderNumber}

mapper.select:
SELECT expressId,expressNo,orderNumber,expressFirm,address,status,addTime,freight/100 freight FROM t_express WHERE 1=1
AND expressFirm=#{relationMap.expressFirm}
AND ( orderNumber LIKE CONCAT(CONCAT('%', #{relationMap.orderNumber}),'%') OR expressNo LIKE CONCAT(CONCAT('%', #{relationMap.orderNumber}),'%') )
