mapper.select:
SELECT c.commodityId,c.yyStock,c.kfStock,c.kxdStock,c.subPrice,c.barCode,c.commodityDes,c.priceSpecification,c.commodityName,c.commodityNo,c.imgMain,c.imgOther,c.specification,c.inPrice/100 inPrice,
c.salePrice/100 salePrice,c.tag,c.categoryId,c.info,c.weight,c.commodityStatus,c.unit,c.stockNum,c.taxRate,
c.status,c.addTime,c.updateTime,c.commodityBanner,c.origin,c.supply,c.hint,c.grade,c.country,c.type,c.storage,t.categoryName
FROM t_commodity c LEFT JOIN t_category t ON c.categoryId=t.categoryId
WHERE 1=1

	AND c.type=#{relationMap.type}


	AND c.tag=#{relationMap.tag}


	AND c.origin=#{relationMap.origin}


	AND t.categoryId=#{relationMap.categoryId}


	AND	c.supply=#{relationMap.supply}


	AND c.commodityStatus=#{relationMap.commodityStatus}


	AND (
	c.barCode LIKE CONCAT(CONCAT('%', #{relationMap.commodityName}),'%')
	OR
	c.commodityName LIKE CONCAT(CONCAT('%', #{relationMap.commodityName}),'%')
	OR
	c.commodityNo LIKE CONCAT(CONCAT('%', #{relationMap.commodityName}),'%')
	)

order by c.addTime desc

SELECT supply FROM t_commodity WHERE 1=1
AND type=#{type}
and status=1 GROUP BY supply

SELECT DISTINCT (origin) FROM t_commodity WHERE 1=1

	AND type=#{type}

and status=1

mapper.update:
UPDATE t_commodity


		status=#{status},

	updateTime=sysdate()

WHERE 1=1

	AND commodityId=#{commodityId}


	AND type=#{type}

UPDATE t_commodity
imgOther=#{imgOther}, commodityName=#{commodityName}, commodityNo=#{commodityNo}, imgMain=#{imgMain}, specification=#{specification}, inPrice=#{inPrice}*100, salePrice=#{salePrice}*100, tag=#{tag}, categoryId=#{categoryId}, info=#{info}, weight=#{weight}, commodityStatus=#{commodityStatus}, unit=#{unit}, stockNum=#{stockNum}, taxRate=#{taxRate}, status=#{status}, commodityBanner=#{commodityBanner}, origin=#{origin}, supply=#{supply}, hint=#{hint}, grade=#{grade}, storage=#{storage}, country=#{country}, commodityDes=#{commodityDes}, subPrice=#{subPrice}, priceSpecification=#{priceSpecification}, type=#{type}, priceSpecificationUnit=#{priceSpecificationUnit}, subPriceUnit=#{subPriceUnit}, updateTime=#{updateTime}
WHERE 1=1
AND commodityId=#{commodityId}
