mapper.update:
		update  t_store  

 			supervisorId = #{supervisorId},
 			process = #{process},
 			remark = #{remark}

 		where  storeId= #{storeId}

mapper.select:
 	SELECT repayId,repayNo,planTotal/100 planTotal,repayMoney/100 repayMoney,remark,
 	(planTotal/100-repayMoney/100) withholdMoney 
	FROM t_repay WHERE repayId=#{0}

mapper.update:
UPDATE t_repay
repayMoney = #{repayMoney}, repayDate = #{repayDate}, status = #{status}, repayType = #{repayType}, updateTime = #{updateTime}
where repayId = #{repayId}
